SELECT s.uuid as survei_uuid, s.survei_type as survei_type, s.description as survei_description, s.version as survei_version,  qb.uuid as questions_bloc_uuid, qb.title as questions_bloc_title, qb.description as questions_bloc_description, qb."order" as questions_bloc_order, qb.tag as questions_bloc_tag, qg.uuid as questions_group_uuid, qg.title as questions_group_title, qg.description as questions_group_description, qg."order" as questions_group_order, qg.tag as questions_group_tag, qi.uuid as questions_item_uuid, qi.title as questions_item_title, qi.description as questions_item_description, qi."order" as questions_item_order, qi.tag as questions_item_tag, qi.validation as questions_item_validation FROM survei s left join questions_bloc qb ON s.uuid = qb.survei_uuid left join questions_group qg on qb.uuid = qg.questions_bloc_uuid left join questions_item qi on qg.uuid = qi.questions_group_uuid left join questions_option qo on qi.uuid = qo.questions_item_uuid